import{_ as l}from"./Blog.vue_vue&type=script&setup=true&lang.39d150f4.js";import{l as t,o as e,m as T,h as a,a as n,k as r}from"./vendor.14139df1.js";import"./app.108283c1.js";const i=n("div",{class:"prose m-auto"},[n("h2",{id:"归纳",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#归纳","aria-hidden":"true"},"#"),r(" 归纳")]),n("ol",null,[n("li",null,"HTTP/2 完全兼容 HTTP/1，是“更安全的 HTTP、更快的 HTTPS”，头部压缩、多路复用等技术可以充分利用带宽，降低延迟，从而大幅度提高上网体验；"),n("li",null,"TCP 协议存在“队头阻塞”，所以 HTTP/2 在弱网或者移动网络下的性能表现会不如 HTTP/1；"),n("li",null,"迁移到 HTTP/2 肯定会有性能提升，但高流量网站效果会更显著；"),n("li",null,"如果已经升级到了 HTTPS，那么再升级到 HTTP/2 会很简单；"),n("li",null,"TLS 协议提供“ALPN”扩展，让客户端和服务器协商使用的应用层协议，“发现”HTTP/2 服务。")]),n("h2",{id:"http2的优点",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#http2的优点","aria-hidden":"true"},"#"),r(" HTTP2的优点")]),n("ol",null,[n("li",null,"http2完全兼容http1，继承了1的优点，规避了一些1的缺点，可以认为是“更安全的 HTTP、更快的 HTTPS”。"),n("li",null,"你应该知道，影响网络速度的两个关键因素是“带宽”和“延迟”，HTTP/2 的头部压缩、多路复用、流优先级、服务器推送等手段其实都是针对这两个要点。"),n("li",null,"节约带宽的基本手段就是压缩，在 HTTP/1 里只能压缩 body，而 HTTP/2 则可以用 HPACK 算法压缩 header，这对高流量的网站非常有价值，有数据表明能节省大概 5%~10% 的流量，这是实实在在的“真金白银”。"),n("li",null,"与 HTTP/1“并发多个连接”不同，HTTP/2 的“多路复用”特性要求对一个域名（或者 IP）只用一个 TCP 连接，所有的数据都在这一个连接上传输，这样不仅节约了客户端、服务器和网络的资源，还可以把带宽跑满，让 TCP 充分“吃饱”。"),n("li",null,"HTTP2只使用一个链接")]),n("h2",{id:"http2的缺点",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#http2的缺点","aria-hidden":"true"},"#"),r(" HTTP2的缺点")]),n("ol",null,[n("li",null,"跟3比起来tcp会有阻塞的情况"),n("li",null,"在切换移动网络是需要从新链接")]),n("h2",{id:"应用层协议协商（alpn）",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#应用层协议协商（alpn）","aria-hidden":"true"},"#"),r(" 应用层协议协商（ALPN）")]),n("p",null,"你有没有想过，在 URI 里用的都是 HTTPS 协议名，没有版本标记，浏览器怎么知道服务器支持 HTTP/2 呢？为什么上来就能用 HTTP/2，而不是用 HTTP/1 通信呢？"),n("p",null,"答案在 TLS 的扩展里，有一个叫“ALPN”（Application Layer Protocol Negotiation）的东西，用来与服务器就 TLS 上跑的应用协议进行“协商”。"),n("p",null,"客户端在发起“Client Hello”握手的时候，后面会带上一个“ALPN”扩展，里面按照优先顺序列出客户端支持的应用协议。"),n("p",null,[n("img",{src:"https://static001.geekbang.org/resource/image/d8/b0/d8f8606948bbd63c31466e464c1956b0.png",alt:""})]),n("p",null,"服务器看到 ALPN 扩展以后就可以从列表里选择一种应用协议，在“Server Hello”里也带上“ALPN”扩展，告诉客户端服务器决定使用的是哪一种。因为我们在 Nginx 配置里使用了 HTTP/2 协议，所以在这里它选择的就是“h2”。"),n("p",null,[n("img",{src:"https://static001.geekbang.org/resource/image/19/a7/19be1138574589458c96040e1a23b3a7.png",alt:""})]),n("p",null,"这样在 TLS 握手结束后，客户端和服务器就通过“ALPN”完成了应用层的协议协商，后面就可以使用 HTTP/2 通信了。")],-1),o={setup(n,{expose:r}){const o={title:"33 | 我应该迁移到HTTP/2吗？",date:"2021-11-17T16:00:00.000Z",duration:"10min",meta:[{property:"og:title",content:"33 | 我应该迁移到HTTP/2吗？"}]};r({frontmatter:o});return t({title:"33 | 我应该迁移到HTTP/2吗？",meta:[{property:"og:title",content:"33 | 我应该迁移到HTTP/2吗？"}]}),(t,n)=>{const r=l;return e(),T(r,{frontmatter:o},{default:a((()=>[i])),_:1})}}};export{o as default};
